const Drout = require('drout');
const React = Drout.React;

class DroutReactPage extends React.Component {
  render() {
    const component = Drout.render(this.props);
    const head = null;
    return (
      <html>
        <head>{head}</head>
        <body>
          <div>
            <link rel="stylesheet" href="/styles.css" type="text/css"></link>
            <div className="drout">{component}</div>
            <script src="/drout.js"></script>
          </div>
        </body>
      </html>
    );
  }
}

module.exports = DroutReactPage;
